class WebSocket {
  constructor() {
    this.OPEN = 1;
    this.readyState = 1;
  }
  on() {}
  send() {}
}

class WebSocketServer {
  constructor() {
    this.clients = new Set();
  }
  on() {}
  handleUpgrade(req, socket, head, cb) {
    if (typeof cb === "function") {
      cb(new WebSocket());
    }
  }
}

module.exports = {
  WebSocket,
  WebSocketServer,
};
